    this.name = name;
    this.type = type;
    this.colors = ['red', 'green', 'black'];
}

function Child() {
    Parent.apply(this, arguments);
    this.owner = true;
}

const a = new Child('a', '1');
console.log(a);

const b = new Child('b', '2');
console.log(b);

